css:
body {
  color: white !important;
  background-color: #030202;
  overflow-x: hidden;
}

#overlay {
  background-color: #030202;
  overflow-x: hidden;
  color: white !important;
}


.ecom-credits{
  display: none;
}

.page-title__head {
  visibility: hidden;
}

.lead {
  visibility: hidden;
}


main .header {
  background-color: black;
}

main .header a {
  color: orange;
}




main #user-button i:before {
  background-color: orange;
  width: 40px
}

main #user-button i:hover:before{
  background-color: white;
  transition: all 0.43s ease-in-out;
}

main #user-button i:hover{
  background-color: orange;
  border-radius: 30px;
  transition: all 0.3s;
  box-shadow: 0 0 5px white;
}




main #cart-button i:before {
  background-color: orange;
  width:40px;
}

main #cart-button i:hover:before{
  background-color: white;
  transition: all 0.43s ease-in-out;
}



.header__toggler-icon {
  color: orange;
}

.header__toggler-icon:hover {
  color: orange;
  transform: scale(1.5);
  transition: all 1s;
}

main #search-input {
  border: none;
  background-color: white;
}

main .top-bar {
  background-color: black;
}

.top-bar__countdown {
  font-weight: 400;
}

main .header__nav a:hover{
  color: orange;
  transform: scale(1.1);
  transition: 1.2s;
}

.menu {
  background-size: cover;
  background-position: center;
  background-position-x: -410px;
  background-image: linear-gradient(rgba(0, 0, 0, 0.3), rgba(0, 0, 0, 0.8)), url(https://i.ibb.co/2N8mx1K/Barco-brazilian-manga-03.webp);
}

.menu__item {
  color: white;
  font-weight: 400;
  background-color: orange;
}

.menu__item:hover {
  color: white;
  background-color: #00826b;
  transition: 0.5s;
}


.minicart__body {
 background-size: cover;
 background-repeat: no-repeat;
 background-image: url(https://i.ibb.co/gRK9JKh/Inserir-um-subt-tulo-12.jpg);
}

.btn {
  color: orange;
}


.btn:not(:disabled):not(.disabled) {
  color: white;
}

span.ml-1.d-none.d-xl-inline-block {
  color: white;
}

a.btn.btn-sm.product__favorite {
  color: black;
}

btn.btn-sm.btn-light.mt-3.offers-notification {
  color: black;
}

.btn.btn-sm.product__favorite {
  background-color: #2b3947;
  color: white !important;
}

.product__favorite i {
   color:white;
   opacity: 1;
}



main .info-bar {
  display: none;
}

.product__sku {
  display: none;
}

.fa-instagram:before, .i-instagram:before {
  color: black;
}

.btn-block {
  opacity: 1;
  background-color: #00a759;
  border: none;
  color: black !important;
}

.btn-block:hover{
  opacity: 1;
  transition: 1s;
  background-color: orange;
  border: none;
  color: black !important;
}

.fade:not(.show) {
  opacity: 1;
}

.product-card__name {
  color: #00826b !important;
  font-size: 18px;
}

.product-card:hover {
  box-shadow: 0 0 4px #00826b;
  transform: scale(0.9);
  transition: all 0.3s ease-in-out;
  text-decoration: none;
  outline: none;
}

.product-card a:hover{
    text-decoration: none;
  outline: none;
}



@media (max-width: 445px) {
  a.top-bar__countdown {
    font-size: 0.73rem;
  }
}

@media (max-width: 991px) {
  button#mobile-search-btn.d-lg-none.btn.btn-lg.collapsed {
    display: none;
  }
}



.btn-sm {
  background-color: #00a759;
  color: white !important;
  border: none;
}

.btn-sm:hover {
  transition: 0.5s;
  color: black !important;
  background-color: orange;
}

.btn-light {
  background-color: #2b3947;
}

.product__name {
  color: white !important;
  font-size: 2.5rem;
  font-weight: 500;
}

.prices__value{
  font-weight: 350;
  color: white;
}

.products-carousel__title {
   display: none;
}

.products-carousel {
  background-color: #030202;
}

.glide__arrow--right i {
  color: #00826b;
}

.glide__arrow--left i {
  color: #00826b;
}



.html-clearfix {
  overflow-x: hidden;
}


.pb-1, .py-1 {
  overflow-x: hidden;
}


main .footer {
    background-color: #030202;
    color: white;
}

.footer__title {
  color: orange;
}

.footer__title:hover {
  color: gold;
}

.footer__title:Hover[aria-expanded] {
  color: gold;
}

a.whatsapp-link {
  color: #fff;
}

.footer__list-icon {
    display: inline-block;
    width: 1.35rem;
    color: orange;
}

a {
  color: orange;
}

.i-instagram:before {
  color: orange;
    opacity: 1;
}

button#go-to-top.btn.btn-primary.ml-3 {
  background-color: orange;
  color: black;
  font-weight: 800;
}

.search-engine {
    position: relative;
    background: black;
}

.search__body {
        min-height: 10rem;
        background: black;
    }

.card-footer:last-child {
    border-radius: 0 0 calc(.4rem - 1px) calc(.4rem - 1px);
    background: black;
}

.search__count {
    color: #ffffff;
    line-height: var(--line-height-lg);
    min-width: 250px;
}

.card-header:first-child {
    border-radius: calc(.4rem - 1px) calc(.4rem - 1px) 0 0;
    background: black;
}

times-circle:before {
    content: "";
    color: white;
}

    .search__box {
        margin-left: 10vw;
        width: 80vw;
        border: 1px solid orange;
    }

.card-body {
    flex: 1 1 auto;
    min-height: 1px;
    padding: 1.25rem;
    background: black;
}

div.product-quickview__body.card-body{
  background: black;
}

.card-header:first-child {
    border-radius: calc(.4rem - 1px) calc(.4rem - 1px) 0 0;
    background: black;
}

.card-header {
    background-color: #383d4408;
    border-bottom: 1px solid rgba(56, 61, 68, .125);
    margin-bottom: 0;
    padding: .75rem 1.25rem;
    color: white;
}

.close {
    color: #ffffff;
    float: right;
    font-size: 1.5rem;
    font-weight: 700;
    line-height: 1;
    /* opacity: .5; */
    text-shadow: 0 1px 0 #fff;
}

.i-envelope:before {
    content: "";
    color: white;
}

button.share-network-sms.btn.share.share--sms.px-2 {
  color: white !important;
}

#product-payament-header.card-header span:hover {
  color: #00826b;
}

.card-body {
    flex: 1 1 auto;
    min-height: 1px;
    padding: 1.25rem;
    background: #fff;
}

.card-header:first-child {
    border-radius: calc(.4rem - 1px) calc(.4rem - 1px) 0 0;
    background: #00826b;
}


.nav-tabs .nav-link {
    background-color: #313131;
    border: 1px solid #0000;
    border-top-left-radius: .4rem;
    border-top-right-radius: .4rem;
    margin-bottom: -1px;
}

.account__greetings h2 {
  color: white !important;
}

.app__footer {
    background: #030202;
    border-top: var(--spacer-1) solid var(--secondary);
}

    .app__credits {
        display: flex;
        justify-content: space-between;
        padding-top: var(--spacer-2);
        color: white;
    }

#storefront-app>div>h1 {
    color: #ffffff;
    font-size: 2rem;
    margin-bottom: 1.5rem;
    text-align: center;
}

.app__header {
    background: #030202;
    box-shadow: var(--box-shadow-sm);
    font-size: var(--font-size-sm);
    padding: var(--spacer-2);
    text-align: center;
}

.app__contact {
    color: #ffffff;
}

.app__contact span:not(:first-child) {
    color: #37b885;
}
