css:
body {
  color: black;
  background-color: white;
}



.ecom-credits{
  display: none;
}

.page-title__head {
  visibility: hidden;
}

.lead {
  visibility: hidden;
}


main .header {
  background-color: black;
}

main .header a {
  color: orange;
}




main #user-button i:before {
  background-color: orange;
  width: 40px
}

main #user-button i:hover:before{
  background-color: white;
  transition: all 0.43s ease-in-out;
}

main #user-button i:hover{
  background-color: orange;
  border-radius: 30px;
  transition: all 0.3s;
  box-shadow: 0 0 5px white;
}




main #cart-button i:before {
  background-color: orange;
  width:40px;
}

main #cart-button i:hover:before{
  background-color: white;
  transition: all 0.43s ease-in-out;
}



.header__toggler-icon {
  color: orange;
}

.header__toggler-icon:hover {
  color: orange;
  transform: scale(1.5);
  transition: all 1s;
}

main #search-input {
  border: none;
  background-color: white;
}

main .top-bar {
  background-color: black;
}

.top-bar__countdown {
  font-weight: 400;
}

main .header__nav a:hover{
  color: orange;
  transform: scale(1.1);
  transition: 1.2s;
}

.menu {
  background-size: cover;
  background-position: center;
  background-position-x: -410px;
  background-image: linear-gradient(rgba(0, 0, 0, 0.3), rgba(0, 0, 0, 0.8)), url(https://i.ibb.co/2N8mx1K/Barco-brazilian-manga-03.webp);
}

.menu__item {
  color: white;
  font-weight: 400;
  background-color: orange;
}

.menu__item:hover {
  color: white;
  background-color: green;
  transition: 0.5s;
  transform: scale(1.1);
}


.minicart__body {
 background-size: contain;
 background-image: url(https://i.ibb.co/gRK9JKh/Inserir-um-subt-tulo-12.jpg);
}

.btn {
  color: orange;
}


.btn:not(:disabled):not(.disabled) {
  color: white;
}

span.ml-1.d-none.d-xl-inline-block {
  color: black;
}

a.btn.btn-sm.product__favorite {
  color: black;
}

btn.btn-sm.btn-light.mt-3.offers-notification {
  color: black;
}

.banner-slider {
  height: auto;
  max-width: 100%;
  width: 100%;
  margin: 0 auto;
  text-align: center;
}

.banner picture img {
    height: auto;
    width: 100%;
}


main .info-bar {
  display: none;
}

.product__sku {
  display: none;
}

.fa-instagram:before, .i-instagram:before {
  color: black;
}


@media (max-width: 991px) {
  .fa-search:before, .i-search:before {
    display: none;
  }
}

