css:
body {
  color: black !important;
  background-color: white;
  overflow-x: hidden;
}

#overlay {
  background-color: white;
  overflow-x: hidden;
  color: black !important;
}


.ecom-credits{
  display: none;
}

.page-title__head {
  visibility: hidden;
}

.lead {
  visibility: hidden;
}


main .header {
  background-color: black;
}

main .header a {
  color: orange;
}




main #user-button i:before {
  background-color: orange;
  width: 40px
}

main #user-button i:hover:before{
  background-color: white;
  transition: all 0.43s ease-in-out;
}

main #user-button i:hover{
  background-color: orange;
  border-radius: 30px;
  transition: all 0.3s;
  box-shadow: 0 0 5px white;
}




main #cart-button i:before {
  background-color: orange;
  width:40px;
}

main #cart-button i:hover:before{
  background-color: white;
  transition: all 0.43s ease-in-out;
}



.header__toggler-icon {
  color: orange;
}

.header__toggler-icon:hover {
  color: orange;
  transform: scale(1.5);
  transition: all 1s;
}

main #search-input {
  border: none;
  background-color: white;
}

main .top-bar {
  background-color: black;
}

.top-bar__countdown {
  font-weight: 400;
}

main .header__nav a:hover{
  color: orange;
  transform: scale(1.1);
  transition: 1.2s;
}

.menu {
  background-size: cover;
  background-position: center;
  background-position-x: -410px;
  background-image: linear-gradient(rgba(0, 0, 0, 0.3), rgba(0, 0, 0, 0.8)), url(https://i.ibb.co/2N8mx1K/Barco-brazilian-manga-03.webp);
}

.menu__item {
  color: white;
  font-weight: 400;
  background-color: orange;
}

.menu__item:hover {
  color: white;
  background-color: #00826b;
  transition: 0.5s;
}


.minicart__body {
 background-size: cover;
 background-repeat: no-repeat;
 background-image: url(https://i.ibb.co/gRK9JKh/Inserir-um-subt-tulo-12.jpg);
}

.btn {
  color: orange;
}


.btn:not(:disabled):not(.disabled) {
  color: white;
}

span.ml-1.d-none.d-xl-inline-block {
  color: black;
}

a.btn.btn-sm.product__favorite {
  color: black;
}

btn.btn-sm.btn-light.mt-3.offers-notification {
  color: black;
}

.btn.btn-sm.product__favorite {
  background-color: #2b3947;
  color: white !important;
}

.product__favorite i {
   color:white;
   opacity: 1;
}



main .info-bar {
  display: none;
}

.product__sku {
  display: none;
}

.fa-instagram:before, .i-instagram:before {
  color: black;
}

.btn-block {
  opacity: 1;
  background-color: #00a759;
  border: none;
  color: black !important;
}

.btn-block:hover{
  opacity: 1;
  transition: 1s;
  background-color: orange;
  border: none;
  color: black !important;
}

.fade:not(.show) {
  opacity: 1;
}

.product-card__name {
  color: #00826b !important;
  font-size: 18px;
}

.product-card:hover {
  box-shadow: 0 0 4px #00826b;
  transform: scale(0.9);
  transition: all 0.3s ease-in-out;
  text-decoration: none;
  outline: none;
}

.product-card a:hover{
    text-decoration: none;
  outline: none;
}



@media (max-width: 445px) {
  a.top-bar__countdown {
    font-size: 0.73rem;
  }
}

@media (max-width: 991px) {
  button#mobile-search-btn.d-lg-none.btn.btn-lg.collapsed {
    display: none;
  }
}


.btn-sm {
  background-color: #00a759;
  color: white !important;
  border: none;
}

.btn-sm:hover {
  transition: 0.5s;
  color: black !important;
  background-color: orange;
}

.btn-light {
  background-color: #00826b;
}

.product__name {
  font-size: 2.5rem;
  font-weight: 500;
}

.prices__value{
  font-weight: 350;
  color: black;
}

.products-carousel__title {
   display: none;
}

.products-carousel {
  background-color: white;
}

.glide__arrow--right i {
  color: #00826b;
}

.glide__arrow--left i {
  color: #00826b;
}



.html-clearfix {
  overflow-x: hidden;
}


.pb-1, .py-1 {
  overflow-x: hidden;
}

.i-whatsapp::before{
  color: green;
}

.card-header:first-child{
  color: black;
}












.fa-shipping-fast:before, .i-shipping-fast:before {
    content: "ï’‹";
    color: black;
}


.quantity-selector__label {
    font-size: 18px;
    line-height: var(--line-height-sm);
    margin-left: var(--spacer-1);
    font-weight: 500;
}

.quantity-selector__input {
    max-width: 2.5rem;
    padding-left: var(--spacer-1);
    padding-right: var(--spacer-1);
    text-align: center;
    color: black;
}

.form-control {
    background-clip: padding-box;
    background-color: #fff;
    border: 1px solid #000000;
    border-radius: .4rem;
    color: #495057;
    display: block;
    font-size: 1rem;
    font-weight: 400;
    height: calc(1.5em + .75rem + 2px);
    line-height: 1.5;
    padding: .375rem .75rem;
    transition: border-color .15s ease-in-out, box-shadow .15s ease-in-out;
    width: 100%;
}

.quantity-selector__item .btn {
    box-shadow: none;
    text-decoration: none;
    background: black;
    margin-left: 5px;
    margin-right: 5px;
}
